using System;

class Program {
    static void Main(string[] args) {
        int n = 5; // Hesaplanacak sayı
        int factorialResult = Factorial(n); // Faktöriyel hesaplama
        int fibonacciResult = Fibonacci(n); // Fibonacci hesaplama

        Console.WriteLine($"Factorial of {n}: {factorialResult}"); // Faktöriyel çıktısı
        Console.WriteLine($"Fibonacci({n}): {fibonacciResult}"); // Fibonacci çıktısı
    }

    // Faktöriyel hesaplama metodu
    static int Factorial(int number) {
        if (number == 0) return 1; // 0! = 1
        return number * Factorial(number - 1); // Ozyineleme
    }

    // Fibonacci hesaplama metodu
    static int Fibonacci(int number) {
        if (number <= 1) return number; // Fibonacci tanımı
        return Fibonacci(number - 1) + Fibonacci(number - 2); // Ozyineleme
    }
}

