class Program
{
    static void Main(string[] args)
    {
        int n = 20; // 20'ye kadar asal sayıları bulmak için üst sınır
        bool isPrime = IsPrime(17); // 17'nin asal olup olmadığını kontrol et
        Console.WriteLine("Is 17 prime? " + isPrime); // 17'nin asal olup olmadığını yazdır

        List<int> primes = GetPrimesUpTo(n); // 20'ye kadar olan asal sayıları al
        Console.WriteLine("Primes up to 20: " + string.Join(", ", primes)); // Asal sayıları yazdır
    }

    // 17'nin asal olup olmadığını kontrol eden metod
    static bool IsPrime(int number)
    {
        if (number <= 1) return false; // 1 ve daha küçük sayılar asal değildir
        for (int i = 2; i <= Math.Sqrt(number); i++) // 2'den başlayarak sayının kareköküne kadar kontrol et
        {
            if (number % i == 0) return false; // Eğer sayı tam bölünüyorsa asal değildir
        }
        return true; // Eğer hiç bölünmediyse asal sayıdır
    }

    // Belirtilen sayıya kadar olan asal sayıları döndüren metod
    static List<int> GetPrimesUpTo(int max)
    {
        List<int> primes = new List<int>(); // Asal sayıları tutacak liste
        for (int i = 2; i <= max; i++) // 2'den başlayarak max'a kadar döngü
        {
            if (IsPrime(i)) primes.Add(i); // Eğer i asal ise listeye ekle
        }
        return primes; // Asal sayılar listesini döndür
    }
}

