using System;

class Program {
    static void Main(string[] args) {
        string str1 = "ABCDGH"; // İlk string
        string str2 = "AEDFHR"; // İkinci string
        int lcsLength = LCS(str1, str2); // LCS uzunluğunu hesapla
        Console.WriteLine($"LCS length: {lcsLength}"); // Sonucu yazdır
    }

    // LCS metodunu tanımlıyoruz
    static int LCS(string str1, string str2) {
        int m = str1.Length; // str1'in uzunluğu
        int n = str2.Length; // str2'nin uzunluğu
        int[,] dp = new int[m + 1, n + 1]; // DP tablosu oluştur

        // DP tablosunu doldur
        for (int i = 1; i <= m; i++) {
            for (int j = 1; j <= n; j++) {
                if (str1[i - 1] == str2[j - 1]) {
                    dp[i, j] = dp[i - 1, j - 1] + 1; // Karakterler eşleşirse
                } else {
                    dp[i, j] = Math.Max(dp[i - 1, j], dp[i, j - 1]); // Eşleşmezse maksimumu al
                }
            }
        }
        return dp[m, n]; // LCS uzunluğunu döndür
    }
}

