class Program {
    static void Main(string[] args) {
        // LRU Cache test edin
        LRUCache cache = new LRUCache(2); // Kapasitesi 2 olan bir LRU Cache oluşturuldu
        Console.WriteLine("Cache created with capacity 2");
        cache.Put(1, 1); // 1 anahtarı ile 1 değerini ekle
        cache.Put(2, 2); // 2 anahtarı ile 2 değerini ekle
        Console.WriteLine("Get(1): " + cache.Get(1)); // 1'i al ve yazdır
        cache.Put(3, 3); // 3 anahtarı ile 3 değerini ekle (1'i silmeli)
        Console.WriteLine("Get(2): " + cache.Get(2)); // 2'yi al ve yazdır (bulunamaz)
        Console.WriteLine("Get(3): " + cache.Get(3)); // 3'ü al ve yazdır
    }
}

public class LRUCache {
    private int capacity;
    private Dictionary<int, int> cache;
    private LinkedList<int> order;

    public LRUCache(int capacity) {
        this.capacity = capacity;
        cache = new Dictionary<int, int>();
        order = new LinkedList<int>();
    }

    public int Get(int key) {
        if (!cache.ContainsKey(key)) {
            return -1; // Anahtar bulunamazsa -1 döner
        }
        // Anahtarı güncelle
        order.Remove(key);
        order.AddFirst(key);
        return cache[key];
    }

    public void Put(int key, int value) {
        if (cache.ContainsKey(key)) {
            cache[key] = value; // Mevcut anahtar güncellenir
            order.Remove(key);
            order.AddFirst(key);
        } else {
            if (cache.Count >= capacity) {
                int leastUsedKey = order.Last.Value; // En az kullanılan anahtar
                order.RemoveLast();
                cache.Remove(leastUsedKey); // En az kullanılanı sil
            }
            cache[key] = value;
            order.AddFirst(key); // Yeni anahtar eklenir
        }
    }
}
