using Microsoft.AspNetCore.Mvc;
using Microsoft.AspNetCore.Builder;
using Microsoft.Extensions.DependencyInjection;

namespace WebAPI.Examples
{
    public class Startup
    {
        public void ConfigureServices(IServiceCollection services)
        {
            // RESTful API Tasarımı için servis kayıtları
            services.AddControllers();

            // RESTful API Tasarımı yapılandırması
            // Gerekli ayarlar burada yapılır
        }

        public void Configure(IApplicationBuilder app)
        {
            // RESTful API Tasarımı middleware'leri
            app.UseRouting();
            app.UseEndpoints(endpoints =>
            {
                endpoints.MapControllers();
            });
        }
    }

    [ApiController]
    [Route("api/[controller]")]
    public class ExampleController : ControllerBase
    {
        // RESTful API Tasarımı kullanım örnekleri
        [HttpGet]
        public IActionResult Get()
        {
            // RESTful API Tasarımı implementasyonu
            return Ok(new { message = "RESTful API Tasarımı örneği" });
        }
    }
}

// RESTful API Tasarımı - İleri Seviye Kullanım Örneği
// Bu örnek, RESTful API Tasarımı konusunun ileri seviye tekniklerini gösterir

using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Logging;
using System;
using System.Threading.Tasks;

namespace WebAPI.Advanced
{
    [ApiController]
    [Route("api/v1/[controller]")]
    public class AdvancedController : ControllerBase
    {
        private readonly ILogger<AdvancedController> _logger;

        public AdvancedController(ILogger<AdvancedController> logger)
        {
            _logger = logger;
        }

        // İleri seviye RESTful API Tasarımı implementasyonu
        [HttpGet("{id}")]
        [ProducesResponseType(200)]
        [ProducesResponseType(404)]
        [ProducesResponseType(500)]
        public async Task<IActionResult> GetAdvanced(int id)
        {
            try
            {
                _logger.LogInformation("RESTful API Tasarımı - Advanced operation started for ID: {Id}", id);

                // İleri seviye RESTful API Tasarımı işlemleri
                // - Async/await pattern kullanımı
                // - Proper error handling
                // - Logging ve monitoring
                // - Response caching
                // - Rate limiting

                return Ok(new { id, message = "Advanced RESTful API Tasarımı implementation" });
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Error in RESTful API Tasarımı operation");
                return StatusCode(500, new { error = "Internal server error" });
            }
        }
    }
}

// RESTful API Tasarımı - Gerçek Dünya Senaryosu
// Bu örnek, RESTful API Tasarımı konusunun production ortamında nasıl kullanıldığını gösterir

using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Logging;
using Microsoft.Extensions.Caching.Memory;
using System;
using System.Threading.Tasks;

namespace WebAPI.Production
{
    [ApiController]
    [Route("api/[controller]")]
    public class ProductionController : ControllerBase
    {
        private readonly ILogger<ProductionController> _logger;
        private readonly IMemoryCache _cache;

        public ProductionController(
            ILogger<ProductionController> logger,
            IMemoryCache cache)
        {
            _logger = logger;
            _cache = cache;
        }

        // Production-ready RESTful API Tasarımı implementasyonu
        [HttpGet("data/{key}")]
        [ResponseCache(Duration = 300)]
        public async Task<IActionResult> GetProductionData(string key)
        {
            // Cache kontrolü
            if (_cache.TryGetValue(key, out var cachedData))
            {
                _logger.LogInformation("Cache hit for key: {Key}", key);
                return Ok(cachedData);
            }

            try
            {
                // Gerçek dünya senaryosu: Database'den veri çekme
                // RESTful API Tasarımı konusunun production'da kullanımı

                var data = await FetchDataFromDatabase(key);

                // Cache'e kaydet
                _cache.Set(key, data, TimeSpan.FromMinutes(5));

                _logger.LogInformation("Data fetched and cached for key: {Key}", key);

                return Ok(data);
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Error fetching data for key: {Key}", key);
                return StatusCode(500, new { 
                    error = "An error occurred",
                    message = "Please try again later" 
                });
            }
        }

        private async Task<object> FetchDataFromDatabase(string key)
        {
            // Simulated database operation
            await Task.Delay(100);
            return new { key, value = "Production data" };
        }
    }
}

