using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Logging;
using Microsoft.Extensions.Caching.Memory;
using System;
using System.Threading.Tasks;

namespace WebAPI.Production
{
    [ApiController]
    [Route("api/[controller]")]
    public class ProductionController : ControllerBase
    {
        private readonly ILogger<ProductionController> _logger;
        private readonly IMemoryCache _cache;

        public ProductionController(
            ILogger<ProductionController> logger,
            IMemoryCache cache)
        {
            _logger = logger;
            _cache = cache;
        }

        // Production-ready HTTP Status Codes implementasyonu
        [HttpGet("data/{key}")]
        [ResponseCache(Duration = 300)]
        public async Task<IActionResult> GetProductionData(string key)
        {
            // Cache kontrolü
            if (_cache.TryGetValue(key, out var cachedData))
            {
                _logger.LogInformation("Cache hit for key: {Key}", key);
                return Ok(cachedData);
            }

            try
            {
                // Gerçek dünya senaryosu: Database'den veri çekme
                // HTTP Status Codes konusunun production'da kullanımı

                var data = await FetchDataFromDatabase(key);

                // Cache'e kaydet
                _cache.Set(key, data, TimeSpan.FromMinutes(5));

                _logger.LogInformation("Data fetched and cached for key: {Key}", key);

                return Ok(data);
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Error fetching data for key: {Key}", key);
                return StatusCode(500, new { 
                    error = "An error occurred",
                    message = "Please try again later" 
                });
            }
        }

        private async Task<object> FetchDataFromDatabase(string key)
        {
            // Simulated database operation
            await Task.Delay(100);
            return new { key, value = "Production data" };
        }
    }
