// Veri erişim katmanı örneği
public interface IUserRepository
{
    Task<User> GetByIdAsync(int id);
    Task<IEnumerable<User>> GetAllAsync();
    Task<User> CreateAsync(User user);
    Task UpdateAsync(User user);
    Task DeleteAsync(int id);
}

public class UserRepository : IUserRepository
{
    private readonly ApplicationDbContext _context;

    public UserRepository(ApplicationDbContext context)
    {
        _context = context;
    }

    public async Task<User> GetByIdAsync(int id)
    {
        return await _context.Users.FindAsync(id);
    }
}

// EF Core kullanım örneği
public class User
{
    public int Id { get; set; }
    public string Name { get; set; }
    public string Email { get; set; }
}

public class ApplicationDbContext : DbContext
{
    public DbSet<User> Users { get; set; }

    protected override void OnConfiguring(DbContextOptionsBuilder optionsBuilder)
    {
        optionsBuilder.UseSqlServer("connection-string");
    }
}

// DbContext örneği
public class ApplicationDbContext : DbContext
{
    public ApplicationDbContext(DbContextOptions<ApplicationDbContext> options)
        : base(options)
    {
    }

    // DbSet'ler - veritabanı tablolarını temsil eder
    public DbSet<User> Users { get; set; }
    public DbSet<Order> Orders { get; set; }
    public DbSet<Product> Products { get; set; }

    // Model yapılandırması
    protected override void OnModelCreating(ModelBuilder modelBuilder)
    {
        // Fluent API ile model yapılandırması
        modelBuilder.Entity<User>(entity =>
        {
            entity.HasKey(e => e.Id);
            entity.Property(e => e.Name).IsRequired().HasMaxLength(100);
        });
    }
}

// DbSet kullanım örnekleri
using (var context = new ApplicationDbContext(options))
{
    // Query (Okuma)
    var users = context.Users.Where(u => u.IsActive).ToList();

    // Create (Ekleme)
    var newUser = new User { Name = "John", Email = "john@example.com" };
    context.Users.Add(newUser);

    // Update (Güncelleme)
    var user = context.Users.Find(1);
    user.Name = "John Updated";

    // Delete (Silme)
    var userToDelete = context.Users.Find(2);
    context.Users.Remove(userToDelete);

    // Değişiklikleri kaydet
    context.SaveChanges();
}

// Program.cs - DbContext DI kaydı
builder.Services.AddDbContext<ApplicationDbContext>(options =>
    options.UseSqlServer(connectionString));

// Controller'da kullanım
public class UsersController : ControllerBase
{
    private readonly ApplicationDbContext _context;

    public UsersController(ApplicationDbContext context)
    {
        _context = context; // DI ile inject edilir
    }

    [HttpGet]
    public async Task<IActionResult> GetUsers()
    {
        var users = await _context.Users.ToListAsync();
        return Ok(users);
    }
}

