// GERÇEK DÜNYA ÖRNEĞİ: E-ticaret Sepet Hesaplama

// ADIM 1: RED - Test yaz
public class ShoppingCartTests
{
    [Fact]
    public void CalculateTotal_SingleItem_ReturnsItemPrice()
    {
        // Arrange
        var cart = new ShoppingCart(); // Henüz yok
        cart.AddItem(new CartItem { ProductId = 1, Quantity = 1, Price = 100 });

        // Act
        decimal total = cart.CalculateTotal();

        // Assert
        Assert.Equal(100, total); // RED: Test başarısız (cart sınıfı yok)
    }

    [Theory]
    [InlineData(100, 0.1, 90)]
    [InlineData(200, 0.2, 160)]
    public void CalculateTotal_WithDiscount_AppliesDiscount(
        decimal price, decimal discountRate, decimal expected)
    {
        // Test multiple scenarios
        var cart = new ShoppingCart();
        cart.AddItem(new CartItem { Price = price, Quantity = 1 });
        cart.SetDiscountRate(discountRate);

        Assert.Equal(expected, cart.CalculateTotal());
    }
}

// ADIM 2: GREEN - Minimal kod yaz
public class ShoppingCart
{
    private readonly List<CartItem> _items = new();
    private decimal _discountRate = 0;

    public void AddItem(CartItem item) => _items.Add(item);

    public void SetDiscountRate(decimal rate) => _discountRate = rate;

    public decimal CalculateTotal()
    {
        decimal subtotal = _items.Sum(item => item.Price * item.Quantity);
        return subtotal * (1 - _discountRate); // GREEN: Testler geçer
    }
}

// ADIM 3: REFACTOR - İyileştir
public class ShoppingCart
{
    // Refactored - daha iyi encapsulation, validation ekle
    private readonly List<CartItem> _items = new();
    private decimal _discountRate = 0;

    public void AddItem(CartItem item)
    {
        if (item == null) throw new ArgumentNullException(nameof(item));
        _items.Add(item);
    }

    // ... diğer refactored kodlar
}

using Xunit;

// ❌ KÖTÜ: Test isolation yok - shared state
public class BadTests
{
    private static int _counter = 0; // Shared state - testler birbirini etkiler

    [Fact]
    public void Test1()
    {
        _counter++;
        Assert.Equal(1, _counter); // İlk çalıştırmada geçer, ikinci çalıştırmada başarısız olabilir
    }

    [Fact]
    public void Test2()
    {
        _counter++;
        Assert.Equal(2, _counter); // Test1'den etkilenir - isolation yok
    }
}

// ✅ İYİ: Test isolation var - her test bağımsız
public class GoodTests
{
    [Fact]
    public void Test1()
    {
        // Arrange - her test kendi verisini oluşturur
        int counter = 0;

        // Act
        counter++;

        // Assert
        Assert.Equal(1, counter); // Her zaman geçer, diğer testlerden etkilenmez
    }

    [Fact]
    public void Test2()
    {
        // Arrange - bağımsız test verisi
        int counter = 0;

        // Act
        counter++;

        // Assert
        Assert.Equal(1, counter); // Test1'den bağımsız çalışır
    }
}

