// C# temel sözdizimi örneği
using System; // Namespace import

namespace MyApplication // Namespace tanımı
{
    class Program // Sınıf tanımı
    {
        static void Main(string[] args) // Ana metot
        {
            // Bu bir yorum satırıdır
            Console.WriteLine("Merhaba Dünya!"); // İfade, noktalı virgül ile biter
        }
    }
}

using System; // 1. Using direktifleri
using System.Collections.Generic;

namespace MyApplication // 2. Namespace
{
    public class Calculator // 3. Sınıf
    {
        // 4. Özellikler (Properties)
        public int Result { get; set; }

        // 5. Metotlar (Methods)
        public int Add(int a, int b) // Metot imzası
        {
            // 6. İfadeler (Statements)
            Result = a + b; // Atama ifadesi
            return Result; // Dönüş ifadesi
        }
    }
}

