// Class tanımlama örneği
public class Car
{
    // Field (alan)
    private string brand;
    private int year;

    // Property (özellik)
    public string Brand
    {
        get { return brand; }
        set { brand = value; }
    }

    public int Year
    {
        get { return year; }
        set { year = value; }
    }

    // Method (metod)
    public void Start()
    {
        Console.WriteLine($"{Brand} {Year} model araba çalıştı.");
    }
}

// Object oluşturma
class Program
{
    static void Main()
    {
        // Object oluşturma (instance)
        Car myCar = new Car();
        myCar.Brand = "Toyota";
        myCar.Year = 2023;

        // Object kullanımı
        myCar.Start(); // Output: Toyota 2023 model araba çalıştı.

        // Başka bir object
        Car yourCar = new Car();
        yourCar.Brand = "Honda";
        yourCar.Year = 2022;
        yourCar.Start(); // Output: Honda 2022 model araba çalıştı.
    }
}

// Class tanımlama syntax'ı
public class Person
{
    // Fields (alanlar)
    private string name;
    private int age;

    // Properties (özellikler)
    public string Name
    {
        get { return name; }
        set { name = value; }
    }

    public int Age
    {
        get { return age; }
        set { age = value; }
    }

    // Methods (metodlar)
    public void Introduce()
    {
        Console.WriteLine($"Merhaba, ben {Name}, {Age} yaşındayım.");
    }
}

// Object oluşturma
class Program
{
    static void Main()
    {
        // new keyword ile object oluşturma
        Person person1 = new Person();
        person1.Name = "Ahmet";
        person1.Age = 25;
        person1.Introduce(); // Output: Merhaba, ben Ahmet, 25 yaşındayım.

        // Başka bir object
        Person person2 = new Person();
        person2.Name = "Ayşe";
        person2.Age = 30;
        person2.Introduce(); // Output: Merhaba, ben Ayşe, 30 yaşındayım.

        // Object'ler bağımsızdır
        Console.WriteLine(person1.Name); // Output: Ahmet
        Console.WriteLine(person2.Name); // Output: Ayşe
    }
}

// Reference type örneği
public class Person
{
    public string Name { get; set; }
    public int Age { get; set; }
}

class Program
{
    static void Main()
    {
        // Reference type: Object heap'te, referans stack'te
        Person person1 = new Person { Name = "Ahmet", Age = 25 };
        Person person2 = person1; // person2, person1'in referansını alır (aynı object)

        person2.Name = "Mehmet"; // person1'in Name'i de değişir!
        Console.WriteLine(person1.Name); // Output: Mehmet (person1 de değişti!)

        // Value type: Değer stack'te saklanır
        int number1 = 10;
        int number2 = number1; // number2, number1'in kopyasını alır

        number2 = 20; // number1 değişmez
        Console.WriteLine(number1); // Output: 10 (number1 değişmedi)

        // Yeni object oluşturma
        Person person3 = new Person { Name = "Ayşe", Age = 30 };
        person1 = person3; // person1 artık person3'ü gösterir
        Console.WriteLine(person1.Name); // Output: Ayşe
        Console.WriteLine(person2.Name); // Output: Mehmet (person2 hala eski object'i gösterir)
    }
}

// Gerçek dünya örneği: E-Ticaret uygulaması

// Product class'ı
public class Product
{
    public int Id { get; set; }
    public string Name { get; set; }
    public decimal Price { get; set; }
    public int Stock { get; set; }

    public bool IsInStock()
    {
        return Stock > 0;
    }

    public void ReduceStock(int quantity)
    {
        if (Stock < quantity)
            throw new InvalidOperationException("Yetersiz stok");
        Stock -= quantity;
    }
}

// Order class'ı
public class Order
{
    public int Id { get; set; }
    public List<Product> Products { get; set; } = new List<Product>();
    public decimal TotalAmount { get; set; }

    public void AddProduct(Product product, int quantity)
    {
        if (!product.IsInStock() || product.Stock < quantity)
            throw new InvalidOperationException($"{product.Name} yetersiz stokta");

        Products.Add(product);
        product.ReduceStock(quantity);
        TotalAmount += product.Price * quantity;
    }
}

// Kullanım
class Program
{
    static void Main()
    {
        // Product object'leri oluştur
        Product laptop = new Product 
        { 
            Id = 1, 
            Name = "Laptop", 
            Price = 15000, 
            Stock = 10 
        };

        Product mouse = new Product 
        { 
            Id = 2, 
            Name = "Mouse", 
            Price = 200, 
            Stock = 50 
        };

        // Order object'i oluştur
        Order order = new Order { Id = 1 };
        order.AddProduct(laptop, 1);
        order.AddProduct(mouse, 2);

        Console.WriteLine($"Sipariş Toplamı: {order.TotalAmount} TL");
        Console.WriteLine($"Laptop Stok: {laptop.Stock}"); // Output: 9 (1 adet satıldı)
    }
}

