// Katmanlı Mimari Örneği

// 1. Presentation Layer (UI Katmanı)
// MyApp.Web/Controllers/ProductController.cs
namespace MyApp.Web.Controllers
{
    public class ProductController : Controller
    {
        private readonly IProductService _productService;

        public ProductController(IProductService productService)
        {
            _productService = productService;
        }

        public IActionResult Index()
        {
            var products = _productService.GetAllProducts();
            return View(products);
        }
    }
}

// 2. Business Layer (İş Mantığı Katmanı)
// MyApp.Business/Services/ProductService.cs
namespace MyApp.Business.Services
{
    public interface IProductService
    {
        List<Product> GetAllProducts();
    }

    public class ProductService : IProductService
    {
        private readonly IProductRepository _repository;

        public ProductService(IProductRepository repository)
        {
            _repository = repository;
        }

        public List<Product> GetAllProducts()
        {
            // İş mantığı burada
            return _repository.GetAll();
        }
    }
}

// 3. Data Access Layer (Veri Erişim Katmanı)
// MyApp.Data/Repositories/ProductRepository.cs
namespace MyApp.Data.Repositories
{
    public interface IProductRepository
    {
        List<Product> GetAll();
    }

    public class ProductRepository : IProductRepository
    {
        private readonly ApplicationDbContext _context;

        public ProductRepository(ApplicationDbContext context)
        {
            _context = context;
        }

        public List<Product> GetAll()
        {
            return _context.Products.ToList();
        }
    }
}

// Solution Yapısı
// MyApp.sln
// ├── MyApp.Web (Presentation Layer)
// ├── MyApp.Business (Business Layer)
// ├── MyApp.Data (Data Access Layer)
// └── MyApp.Core (Domain Models)

// Program.cs - Dependency Injection Yapılandırması
var builder = WebApplication.CreateBuilder(args);

// Business Layer servislerini kaydet
builder.Services.AddScoped<IProductService, ProductService>();

// Data Access Layer servislerini kaydet
builder.Services.AddScoped<IProductRepository, ProductRepository>();

// Entity Framework DbContext'i kaydet
builder.Services.AddDbContext<ApplicationDbContext>(options =>
    options.UseSqlServer(builder.Configuration.GetConnectionString("DefaultConnection")));

var app = builder.Build();

// Solution Yapısı Örneği
// MyApp.sln
// ├── MyApp.Web (ASP.NET Core Web App)
// ├── MyApp.Business (Class Library)
// ├── MyApp.Data (Class Library)
