// Dependency Injection Örneği

// ❌ YANLIŞ: Tight Coupling
public class ProductService
{
    private ProductRepository _repository = new ProductRepository(); // Sıkı bağlantı

    public List<Product> GetProducts()
    {
        return _repository.GetAll();
    }
}

// ✅ DOĞRU: Dependency Injection
public class ProductService
{
    private readonly IProductRepository _repository;

    public ProductService(IProductRepository repository) // DI
    {
        _repository = repository;
    }

