// ADIM 2: GREEN - Minimal kod yaz
public class ShoppingCart
{
    private readonly List<CartItem> _items = new();
    private decimal _discountRate = 0;

    public void AddItem(CartItem item) => _items.Add(item);

    public void SetDiscountRate(decimal rate) => _discountRate = rate;

    public decimal CalculateTotal()
    {
        decimal subtotal = _items.Sum(item => item.Price * item.Quantity);
        return subtotal * (1 - _discountRate); // GREEN: Testler geÃ§er
    }
}

