// Login endpoint - Cookie oluÅŸtur
[HttpPost("login")]
[AllowAnonymous]
public async Task<IActionResult> Login([FromBody] LoginModel model)
{
    var user = await _userService.ValidateUser(model.Username, model.Password);

    if (user == null)
    {
        return Unauthorized(new { message = "Invalid credentials" });
    }

    // Claims oluÅŸtur
    var claims = new List<Claim>
    {
        new Claim(ClaimTypes.NameIdentifier, user.Id.ToString()),
        new Claim(ClaimTypes.Name, user.Username),
        new Claim(ClaimTypes.Email, user.Email),
        new Claim(ClaimTypes.Role, user.Role)
    };

    // ClaimsIdentity ve ClaimsPrincipal oluÅŸtur
    var claimsIdentity = new ClaimsIdentity(claims, CookieAuthenticationDefaults.AuthenticationScheme);
    var authProperties = new AuthenticationProperties
    {
        IsPersistent = model.RememberMe, // "Beni hatÄ±rla" Ã¶zelliÄŸi
        ExpiresUtc = DateTimeOffset.UtcNow.AddMinutes(30)
    };

    // Cookie'yi oluÅŸtur ve yanÄ±tla
    await HttpContext.SignInAsync(
        CookieAuthenticationDefaults.AuthenticationScheme,
        new ClaimsPrincipal(claimsIdentity),
        authProperties
    );

    return Ok(new { message = "Login successful" });
}

