// Console Project Yapısı - Gerçek Dünya Senaryosu
// Bu örnek, Console Project Yapısı konusunun production ortamında nasıl kullanıldığını gösterir

using System;
using System.Threading.Tasks;
using Microsoft.Extensions.Logging;
using Microsoft.Extensions.Configuration;
using Microsoft.Extensions.DependencyInjection;
using Microsoft.Extensions.Hosting;

namespace ConsoleApp.Production
{
    class Program
    {
        static async Task Main(string[] args)
        {
            var host = Host.CreateDefaultBuilder(args)
                .ConfigureAppConfiguration((context, config) =>
                {
                    // Configuration yönetimi
                    config.AddJsonFile("appsettings.json", optional: false);
                    config.AddEnvironmentVariables();
                })
                .ConfigureServices((context, services) =>
                {
                    // Production-ready Console Project Yapısı yapılandırması
                    services.AddLogging(configure =>
                    {
                        configure.AddConsole();
                        configure.AddDebug();
                    });

                    // Console Project Yapısı için production servisleri
                    // Error handling, retry policies, circuit breakers
                })
                .UseConsoleLifetime()
                .Build();

            var logger = host.Services.GetRequiredService<ILogger<Program>>();
            var config = host.Services.GetRequiredService<IConfiguration>();

            try
            {
                logger.LogInformation("Application starting...");

                // Production-ready Console Project Yapısı implementasyonu
                // - Configuration management
                // - Error handling ve retry logic
                // - Logging ve monitoring
                // - Resource cleanup

                await host.RunAsync();

                logger.LogInformation("Application stopped gracefully");
            }
            catch (Exception ex)
            {
                logger.LogCritical(ex, "Application terminated unexpectedly");
                throw;
            }
            finally
            {
                // Resource cleanup
                host.Dispose();
            }
        }
    }
}

