// API örneği - Basit bir endpoint
[ApiController]
[Route("api/[controller]")]
public class UsersController : ControllerBase
{
    [HttpGet]
    public IActionResult GetUsers()
    {
        var users = new[]
        {
            new { Id = 1, Name = "John", Email = "john@example.com" },
            new { Id = 2, Name = "Jane", Email = "jane@example.com" }
        };

        return Ok(users);
    }
}

// RESTful API örneği
[ApiController]
[Route("api/[controller]")]
public class ProductsController : ControllerBase
{
    // GET /api/products - Tüm ürünleri getir
    [HttpGet]
    public IActionResult GetProducts() { }

    // GET /api/products/1 - Belirli bir ürünü getir
    [HttpGet("{id}")]
    public IActionResult GetProduct(int id) { }

    // POST /api/products - Yeni ürün oluştur
    [HttpPost]
    public IActionResult CreateProduct(Product product) { }

    // PUT /api/products/1 - Ürünü güncelle
    [HttpPut("{id}")]
    public IActionResult UpdateProduct(int id, Product product) { }

    // DELETE /api/products/1 - Ürünü sil
    [HttpDelete("{id}")]
    public IActionResult DeleteProduct(int id) { }
}

// Program.cs - Web API yapılandırması
var builder = WebApplication.CreateBuilder(args);

// Add services to the container
builder.Services.AddControllers();
// Learn more about configuring Swagger/OpenAPI at https://aka.ms/aspnetcore/swashbuckle
builder.Services.AddEndpointsApiExplorer();
builder.Services.AddSwaggerGen();

var app = builder.Build();

// Configure the HTTP request pipeline
if (app.Environment.IsDevelopment())
{
    app.UseSwagger();
    app.UseSwaggerUI();
}

app.UseHttpsRedirection();
app.UseAuthorization();
app.MapControllers();

app.Run();
