// Void metot (değer döndürmez)
static void Selamla(string isim)
{
    Console.WriteLine($"Merhaba {isim}!");
}

// Değer döndüren metot
static int Topla(int a, int b)
{
    return a + b;
}

// Metot çağırma
Selamla("Ahmet");
int sonuc = Topla(5, 3); // 8

// Method overloading
static int Carp(int a, int b)
{
    return a * b;
}

static int Carp(int a, int b, int c)
{
    return a * b * c;
}

// Optional parameters (C# 4.0+)
static void Yazdir(string mesaj, bool buyukHarf = false)
{
    if (buyukHarf)
        Console.WriteLine(mesaj.ToUpper());
    else
        Console.WriteLine(mesaj);
}

// Named parameters
Yazdir("Merhaba", buyukHarf: true);

